#!/bin/bash#By wocin#Email ---#-------------------I am boring line-----------------------------------groupadd zabbixuseradd -g zabbix zabbixusermod -s /sbin/nologin zabbixcd /usr/local/lnmp/wget -v http://jaist.dl.sourceforge.net/project/zabbix/ZABBIX%20Latest%20Stable/2.2.1/zabbix-2.2.1.tar.gztar xvf zabbix-2.2.1.tar.gzcd zabbix-2.2.1./configure  --enable-agentmakemake installcp misc/init.d/tru64/zabbix_agentd /etc/init.d/chmod +x /etc/init.d/zabbix_agentd#-------------------I am boring line-----------------------------------sed -i '/#!\/bin/a\#chkconfig: 35 95 95\n#description:zabbix Agent server' /etc/init.d/zabbix_agentdchkconfig  --add zabbix_agentdecho '''LogFile=/var/log/zabbix_agentd.logServer=192.168.1.111 #server IPHostname=web2.seegame.com #client hostname''' >/etc/zabbix_agentd.conf